sections.code:
body {
        font-family: "Helvetica Neue", Helvetica, Arial, sans-serif !important;
        color: #ddd;
        background-color: #222 !important;
    }

    a {
        color: #608dff !important;
        text-decoration: none;
    }

    a:hover {
        color: #637ab5 !important;
        text-decoration: underline;
    }

    a:visited {
        color: #637ab5 !important;
    }

    .b, div, th {
        color : #fff !important;
    }

    h1 {
        border: 0 !important;
        font-family: 'Arial' !important;
        font-weight: 700 !important;
        font-size: 36px !important;
        color: #fff !important;
    }

    h2 {
        border: 0 !important;
        font-family: 'Arial' !important;
        font-weight: 400 !important;
        font-size: 26px !important;
        color: #eee !important;
    }

    h3 {
        border: 0 !important;
        font-family: 'Arial' !important;
        font-weight: 400 !important;
        font-size: 16px !important;
        color: #ddd !important;
    }

    h4 {
        color: #ccc !important;
    }

    [style*="color: black"] {
        color: #fff !important;
    }

    [style*="background:#f8f9fa"] {
        background: #2d2c2b !important;
        border: none !important;
    }

    .reflist,
    .refbegin {
        background-color: #282828 !important;
    }

    #content h3 {
        font-size: 20px !important;
    }

    p {
        font-weight: 300 !important;
        line-height: 1.7 !important;
        color: #ddd !important;
    }

    .portal-column-right span,
    .portal-column-left span,
    body.ns-100 .mw-body-content h2,
    .mw_metadata th,
    .tracklist th,
    #mp-tfl-h2 {
        color: #ddd !important;
        background-color: #444 !important;
    }

    body.ns-100 .mw-body-content th,
    body.ns-100 .mw-body-content div,
    body.ns-100 #portal td,
    .mw_metadata td,
    .tracklist td,
    .tracklist div {
        color: #ddd !important;
        border-width: 0px !important;
        background-color: #333 !important;
    }

    .portal-column-right,
    .portal-column-left,
    body.ns-100 .mw-body-content .subpages {
        color: #ddd !important;
        background-color: #222 !important;
        border-width: 0 !important;
    }

    .mw-highlight {
        background-color: #222 !important;
    }

    .mw-highlight .nc,
    .mw-highlight .nf {
        color: #068dff;
    }

    .mw-highlight .no {
        color: #dd0000;
    }

    .mw-notification {
        background-color: #333 !important;
    }

    #Example {
        color: #ddd !important;
    }

    #disambigbox {
        width: 99% !important;
        border-width: 1px 0 1px 0 !important;
        border-style: solid !important;
        border-color: #777 !important;
    }

    .hlist.hlist-separated.inline {
        background-color: transparent !important;
    }

    table,
    td,
    th {
        border: 0 !important;
        border-collapse: collapse !important;
        padding: 5px !important;
        /* color: #333 !important; */

        background-color: #444 !important;
        /* With this feature enabled, you loose visibility on colored table cells */
    }

