        -webkit-filter: invert(100%);
        filter: invert(100%);
    }

    div[style*="background:#FCFCFC"] {
	    background:#263F1E !important
    }

    /* Preferences */

    .client-js #preferences {
        border: 1px solid #888 !important;
        background-color: #444 !important;
    }

    .client-js #preferences legend {
        color: #ededed;
    }

    .mw-prefs-tabs-wrapper.oo-ui-panelLayout-framed {
        border-color: #888 !important;
    }

    /* Page Preview Options dialog */
    #mwe-popups-settings {
        background-color: #333 !important;
    }

    #mwe-popups-settings main form label > span {
        color: #fff !important;
    }

    .mw-ui-icon-popups-close::before {
        filter: invert(100%) !important;
    }

    .mwe-popups-overlay {
        background-color: rgba(0, 0, 0, 0.6) !important;
    }

    /* Minor fixes */

    .app-badges .footer-sidebar-content {
        background-color: #222 !important;
    }

    .mw-mmv-info-box {
        background-color: #111 !important;
    }

    :root {
        --wiki-logo: url("data:image/png;base64,");
    }
}

@-moz-document domain("m.wikipedia.org") {
    /* Mobile version */

    #mw-mf-page-center {
        background: none !important;
    }

    .hatnote.navigation-not-searchable {
        background: transparent !important;
    }

    .header-container.header-chrome {
        background-color: #222 !important;
    }

    .branding-box img {
        filter: invert(100%);
    }

    .minerva__tab-container .minerva__tab.selected {
        background-color: #444;
        border-bottom: 2px solid #666;
        padding: 6px;
    }
}

@-moz-document domain("fr.wikipedia.org"), domain("fr.m.wikipedia.org") {
    /* French version */

    #accueil_2017_en-tete {
        background-image: none !important;
    }

    .mw-logo-container, .mw-ui-icon, .searchButton {
        filter: invert(100%);
    }

    .mw-article-toolbar-container {
        background: none !important;
    }

    #mw-head {
        height: 40px !important;
    }

    .mw-body {
        margin-top: 1px !important;
        border-top: none;
    }

    .bandeau-section {
        background-color: #333 !important;
        border: 1px solid #666 !important;
     }

    .autres-projets {
        background-color: #333 !important;
        border: 1px solid #666 !important;
    }

    .mw-page-container {
        background-color: #222 !important;
        border: none;
    }

    .mw-widget-dateInputWidget-handle,
    .oo-ui-widget,
    .legendtextlike,
    .wvui-input__input,
    .wvui-button,
    .wvui-typeahead-search__suggestion,
    .wvui-typeahead-suggestion__title,
    .wvui-typeahead-search__suggestions__footer {
        background-color: #333 !important;
        color: #ddd !important;
    }

    .wvui-typeahead-suggestion__thumbnail {
        background-color: #aaa !important; /* Temporary filling while fixing the thumbnail previews in searchbar */
    }

    #mw-panel.mw-sidebar {
        background: #222 !important;
    }

    .bloc {
        background-color: #444 !important;
    }

    #left-navigation {
        margin-left: 0 !important;
    }
}

@-moz-document domain("de.wikipedia.org"), domain("de.m.wikipedia.org") {
    /* German version */

    #hauptseite h2 {
        background: inherit;
        border: none;
    }

    #hauptseite .inhalt {
        background: inherit;
        border: none;
    }

    #qualitaetssicherungstext {
        background-color: #444 !important;
        border: 1px solid #666 !important;
    }
}

