    border-color: var(--bg-5) !important;
  }
  body .bordered-list > li,
  body .unstyled-list > li {
    border-color: var(--bg-5) !important;
  }
  html {
    scrollbar-color: var(--bg-4) var(--bg-2);
  }
  ::-webkit-scrollbar {
    width: 12px;
  }
  ::-webkit-scrollbar-track {
    background: var(--bg-2);
  }
  ::-webkit-scrollbar-thumb {
    background: var(--bg-4);
  }
  body ::placeholder {
    color: var(--fg-4) !important;
  }
  body ::-webkit-input-placeholder {
    color: var(--fg-4) !important;
  }
  body .page-wrap {
    background-color: transparent !important;
  }
  body .page-wrap .brand-holder p {
    color: var(--fg-3) !important;
  }
  body .page-wrap hr.footer-fixed {
    padding-top: 24px;
    margin-bottom: 0;
    background-color: var(--bg-1) !important;
  }
  body .page-wrap hr.footer-fixed + .footer-container {
    background-color: transparent !important;
  }
  body .page-wrap .login-box,
  body .page-wrap .signup-box,
  body .page-wrap .omniauth-container {
    border-radius: 4px;
    border-color: var(--bg-5) !important;
    background-color: var(--bg-2) !important;
    box-shadow: inset 0 0 0 1px var(--bg-5) !important;
  }
  .omniauth-btn {
    background-color: var(--bg-1) !important;
  }
  .omniauth-btn:hover {
    background-color: var(--bg-3) !important;
  }
  .omniauth-divider::before,
  .omniauth-divider::after {
    border-color: var(--bg-5) !important;
  }
  body .page-wrap .login-box input.form-control,
  body .page-wrap .signup-box input.form-control,
  body .page-wrap .omniauth-container input.form-control {
    background-color: var(--bg-1) !important;
  }
  .devise-layout-html body.navless {
    height: calc(100% - 41px);
  }
  .fa {
    color: var(--fg-4) !important;
  }
  .fa.fa-triangle,
  .fa.merge-icon {
    color: var(--v2-5) !important;
  }
  .fa.fa-bars:not(#z) {
    color: var(--fg-1) !important;
  }
  .legend-success .fa-circle,
  .fa.checkmark {
    color: var(--v4-5) !important;
  }
  .fa.fa-certificate {
    color: var(--ac) !important;
  }
  .fa[class *= 'paused'] {
    color: var(--v1-5) !important;
  }
  .fa[class *= 'online'] {
    color: var(--v4-5) !important;
  }
  @supports not (-moz-user-select: none) {
    body .created-icon,
    body .opened-icon,
    body .ic-upload,
    body .gl-icon[data-testid *= 'status_'],
    body [class *= 'ci-status'] svg {
      filter: invert(0.85) hue-rotate(180deg) saturate(100%);
    }
    body :not(#z) .ci-status-icon-created svg,
    body :not(#z) .ci-status-icon-skipped svg {
      fill: #333 !important;
    }
  }
  body .bs-callout {
    border-radius: 4px;
    color: var(--fg-1) !important;
    border-color: var(--bg-5) !important;
    background-color: var(--bg-3) !important;
  }
  body .bs-callout a:not(.btn) {
    color: var(--ac) !important;
  }
  body .bs-callout strong {
    color: inherit !important;
  }
  body .bs-callout-warning {
    color: var(--v9-8) !important;
    border-color: var(--v2-5) !important;
    background-color: var(--v2-1) !important;
  }
  body .bs-callout-success {
    color: var(--v9-8) !important;
    border-color: var(--v4-5) !important;
    background-color: var(--v4-1) !important;
  }
  body .bs-callout-danger {
    color: var(--v9-8) !important;
    border-color: var(--v1-5) !important;
    background-color: var(--v1-1) !important;
  }
  body .bs-callout-info {
    color: var(--v9-8) !important;
    border-color: var(--v6-5) !important;
    background-color: var(--v6-1) !important;
  }
  body .bs-callout .btn-default,
  body .bs-callout code:not([class]) {
    background-color: var(--bg-1) !important;
  }
  body .bs-callout.help-callout,
  body .bs-callout.shared-runners-description {
    color: var(--fg-1) !important;
    border-color: var(--bg-5) !important;
    background-color: var(--bg-2) !important;
  }
  body .bs-callout.help-callout code,
