  }
  body hr {
    border-color: var(--bg-3);
  }
  body h1,
  body .h1,
  body h2,
  body .h2,
  body h3,
  body .h3,
  body h4,
  body .h4,
  body h5,
  body .h5,
  body h6,
  body .h6 {
    color: var(--fg-1);
  }
  body time {
    color: inherit;
  }
  body :not(pre) > code {
    color: var(--fg-1);
    background-color: var(--bg-2);
    box-shadow: inset 0 0 0 1px var(--bg-5);
  }
  body pre {
    color: var(--fg-1);
    border-color: var(--bg-5);
    background-color: var(--bg-2);
  }
  body pre[class*="description"] {
    border-left-width: 4px;
    font-size: 0.875rem;
    color: var(--fg-2) !important;
    border-color: var(--bg-5) !important;
  }
  body > pre:not([class]) {
    background-color: transparent !important;
  }
  body kbd {
    border-radius: 3px;
