@-moz-document domain('next.gitlab.com') {
  body {
    background: linear-gradient(130deg, var(--bg-2), var(--bg-1)) !important;
  }
  .wrapper {
    padding: 1rem;
    min-height: calc(100vh - 2rem);
  }
  .wrapper h1,
  .wrapper p {
    color: var(--fg-1) !important;
  }
  .wrapper a {
    color: var(--v0-9) !important;
  }
  .wrapper a:hover {
    color: var(--v0-7) !important;
  }
  .wrapper * {
    font-family: var(--ui-font) !important;
  }
  .wrapper .bg {
    filter: grayscale(1) invert(0.5) opacity(0.2);
  }
  .wrapper [class*='box'] {
    border-radius: 4px !important;
    background-color: var(--bg-2) !important;
    box-shadow: inset 0 0 0 1px var(--bg-5) !important;
  }
  .wrapper [class*='box'] .text-switch {
    color: var(--fg-3) !important;
  }
  .wrapper [class*='box'] [class*='toggle'] + label {
    background-color: var(--v0-9) !important;
  }
  .wrapper [class*='box'] [class*='toggle'] + label::after {
    background-color: var(--fg-0) !important;
  }
  .wrapper [class*='box'] [class*='toggle'] + label::before {
    background-color: var(--bg-1) !important;
  }
  .wrapper [class*='box'] [class*='toggle']:not(:checked) + label {
    background-color: var(--bg-5) !important;
  }
  .wrapper [class*='box'] [class*='toggle']:checked + label::before {
    background-color: var(--v0-5) !important;
  }
}

/* ==UserStyle==
@name         Stylus Dark
@version      1.3.4
@description  Dark Stylus theme based on ShadowFox
@namespace    https://github.com/overdodactyl/Stylus-Dark
@author       overdodactyl
@homepageURL  https://github.com/overdodactyl/Stylus-Dark
@supportURL   https://github.com/overdodactyl/Stylus-Dark/issues
@updateURL    https://raw.githubusercontent.com/overdodactyl/Stylus-Dark/master/stylus-dark.user.css 
@license      CC-BY-SA-4.0
==/UserStyle== */
@-moz-document regexp("chrome-extension://.*"),
regexp("moz-extension://.*") {
  #notes,
  body {
    background: #38383d!important;
    color: #b1b1b3!important
  }
  #header {
    color: silver!important;
    background: #2a2a2e!important;
    border-style: none!important
  }
  #stylus-popup {
    background: #4a4a4f!important;
    color: silver!important
  }
  #hotkey-info[data-active] {
    border-left: 2px solid #2a2a2e!important
  }
  #hotkey-info div {
    border-top: 1px solid #2a2a2e!important;
    background-color: #2a2a2e!important
  }
  #help-popup,
  #sections,
  .newUI .target {
    color: #b1b1b3!important
  }
  #help-popup,
  #message-box > div,
  #message-box-buttons,
  #message-box-title,
  body {
    background: #2a2a2e!important
  }
  .search-result {
    background: #4a4a4f!important;
    border-color: #737373!important
  }
  .search-result:hover {
    border-color: #b1b1b3!important
  }
  .search-result-meta {
    background-color: #4a4a4f!important;
    color: #b1b1b3!important
  }
  button,
  input:not([type]),
  select {
    font-family: arial,sans-serif!important
  }
  input[type=checkbox]:not(.slider) {
    background: #38383d!important
  }
  .firefox select,
  .sorter-selection select,
  input:not(.slider) {
    background-color: #38383d!important;
    color: silver!important;
    border: 1px solid #4a4a4f!important
  }
  .filter-selection select {
    background-color: transparent!important;
    border: none!important
  }
  #filters .filter-selection:hover {
    background-color: transparent!important
  }
  button {
    background: #2a2a2e!important;
    border: 1px solid #484848!important;
    color: silver!important;
    border-radius: 3px!important
  }
  .svg-icon.checked {
    fill: #45a1ff!important
  }
  button:hover {
    color: #45a1ff!important
  }
  a,
  a:visited {
    color: #e1e1e1!important
  }
  .dropdown-content {
    background: #2a2a2e!important
  }
  .dropdown-content a:hover {
    background: #2a2a2e!important;
    color: #45a1ff!important
  }
  #save-button:lang(en):after {
    content: 'd'!important
  }
  body.dirty #save-button:lang(en):after {
    content: '?'!important
  }
  .newUI .entry:hover .svg-icon:hover,
  .svg-icon:hover {
    fill: #45a1ff!important
  }
  #message-box.danger #message-box-title {
    background-color: #b22222!important
  }
  .newUI .can-update .update,
  .newUI .no-update:not(.update-problem):not(.update-done) .up-to-date {
    color: #000!important
  }
  .svg-icon {
    fill: #b1b1b3!important
  }
  #url {
    filter: invert(65%)!important
  }
  .CodeMirror-gutters {
    color: #b1b1b3!important
  }
  .newUI .entry {
    background: #38383d!important
  }
  .newUI .entry:hover {
    background: #2a2a2e!important
  }
  .disabled h2::after {
    border: 1px solid #444!important
  }
  .svg-icon.select-arrow {
