    }

    .ui-template-list .list-group-item .dropdown-container:hover {
        background-color: #2a2a2a !important;
    }

    .template-menu .more-template {
        border-color: #6d6d6d;
    }

    .template-menu .more-template:hover {
        color: #eee;
        border-color: #6d6d6d;
    }

    /*--------------- code mirror ---------------*/
    .modal-content {
        background: #1f2226;
    }

    .modal-header {
        border-bottom: 1px solid #46484f;
    }

    .modal-footer {
        border-top: 1px solid #46484f;
    }

    a.list-group-item {
        background: #1f2226;
        color: #b8bfc6;
        border: 1px solid #46484f;
    }

    a.list-group-item .list-group-item-heading {
        color: #b8bfc6;
    }

    a.list-group-item:focus,
    a.list-group-item:hover {
        background: #434651;
        color: #b8bfc6;
    }

    button.close {
        color: #b8bfc6;
        opacity: .5;
    }

    .close:focus,
    .close:hover {
        color: #fff;
        opacity: .8;
    }

    .CodeMirror {
        background: #1f2226;
    }

    .CodeMirror-gutters {
        background: #1f2226;
        border-right: 1px solid rgba(204, 217, 255, 0.1);
    }

    .cm-s-default .cm-comment {
        color: #888;
    }

    .cm-s-default .cm-quote {
        color: #b8bfc6;
    }

    .cm-s-default .cm-header {
        color: #ffa653;
    }

    .cm-s-default .cm-link {
        color: #b0ee83;
    }

    .cm-s-default .cm-string,
    .cm-s-default .cm-variable-2 {
        color: #7bf;
    }

    .cm-s-default .cm-def {
        color: #c678dd;
    }

    .cm-s-default .cm-number,
    .cm-s-default .cm-attribute,
    .cm-s-default .cm-qualifier,
    .cm-s-default .cm-plus,
    .cm-s-default .cm-atom {
        color: #eda35e;
    }

    .cm-s-default .cm-property,
    .cm-s-default .cm-variable,
    .cm-s-default .cm-variable-3,
    .cm-s-default .cm-operator,
    .cm-s-default .cm-bracket {
        color: #f76e79;
    }

    .cm-s-default .cm-keyword,
    .cm-s-default .cm-builtin,
    .cm-s-default .cm-tag {
        color: #98c379;
    }

    .modal-title {
        color: #6a6a6a;
    }

    .modal-body {
        color: #6a6a6a !important;
    }

    div[contenteditable]:empty:not(:focus):before {
        color: #aaa;
    }

    .CodeMirror pre {
        color: #b8bfc6;
    }

    .CodeMirror pre span[style^="background-color: rgb(221, 251, 230)"] {
        background-color: #288c27 !important;
    }

    .CodeMirror pre span[style^="background-color: rgb(249, 215, 220)"] {
        background-color: #a52721 !important;
    }

    /*------- code highlight: Visual Stutdio Code theme for highlight.js -------*/
    .hljs {
        background: #1E1E1E;
        color: #DCDCDC;
    }

    .hljs-keyword,
    .hljs-literal,
    .hljs-symbol,
    .hljs-name {
        color: #569CD6;
    }

    .hljs-link {
        color: #569CD6;
        text-decoration: underline;
    }

    .hljs-built_in,
    .hljs-type {
        color: #4EC9B0;
    }

    .hljs-number,
    .hljs-class {
        color: #B8D7A3;
    }

    .hljs-string,
    .hljs-meta-string {
        color: #D69D85;
    }

    .hljs-regexp,
    .hljs-template-tag {
        color: #d16969;
    }

    .hljs-title {
        color: #dcdcaa;
    }

    .hljs-subst,
    .hljs-function,
    .hljs-formula {
        color: #DCDCDC;
    }

    .hljs-comment,
    .hljs-quote {
        color: #57A64A;
    }

    .hljs-doctag {
        color: #608B4E;
    }

    .hljs-meta,
    .hljs-meta-keyword,
    .hljs-tag {
        color: #9B9B9B;
    }

    .hljs-variable,
    .hljs-template-variable {
        color: #BD63C5;
    }

    .hljs-params,
    .hljs-attr,
    .hljs-attribute,
    .hljs-builtin-name {
        color: #9CDCFE;
    }

    .hljs-section {
        color: gold;
    }

    .hljs-emphasis {
        font-style: italic;
    }

    .hljs-strong {
        font-weight: bold;
    }

    /*
                      .hljs-code {
                        font-family:'Monospace';
                      }
                      */
    .hljs-bullet,
    .hljs-selector-tag,
    .hljs-selector-id,
    .hljs-selector-class,
    .hljs-selector-attr,
    .hljs-selector-pseudo {
        color: #D7BA7D;
    }

    .hljs-addition {
        background-color: #155a36;
        color: #dfdfdf;
        display: inline-block;
        width: 100%;
    }

    .hljs-deletion {
        background-color: #872e2e;
        color: #dfdfdf;
        display: inline-block;
        width: 100%;
    }

    /*---------- code highlight: Visual Stutdio Code theme for Prism.js ----------*/
    code[class*="language-"],
    pre[class*="language-"] {
        color: #DCDCDC;
    }

    :not(pre)>code[class*="language-"],
    pre[class*="language-"] {
        background: #1E1E1E;
    }

    .token.comment,
    .token.block-comment,
    .token.prolog,
    .token.cdata {
        color: #57A64A;
    }

    .token.doctype,
    .token.punctuation {
        color: #9B9B9B;
    }

    .token.tag,
    .token.entity {
        color: #569CD6;
    }

    .token.attr-name,
    .token.namespace,
    .token.deleted,
    .token.property,
    .token.builtin {
        color: #9CDCFE;
    }

    .token.function,
    .token.function-name {
        color: #dcdcaa;
    }

    .token.boolean,
    .token.keyword,
    .token.important {
        color: #569CD6;
    }

    .token.number {
        color: #B8D7A3;
    }

    .token.class-name,
    .token.constant {
        color: #4EC9B0;
    }

    .token.symbol {
        color: #f8c555;
    }

    .token.rule {
        color: #c586c0;
    }

    .token.selector {
        color: #D7BA7D;
    }

    .token.atrule {
        color: #cc99cd;
    }

    .token.string,
    .token.attr-value {
        color: #D69D85;
    }

    .token.char {
        color: #7ec699;
    }

    .token.variable {
        color: #BD63C5;
    }

    .token.regex {
        color: #d16969;
    }

    .token.operator {
        color: #DCDCDC;
        background: transparent;
    }

    .token.url {
        color: #67cdcc;
    }

    .token.important,
    .token.bold {
        font-weight: bold;
    }

    .token.italic {
        font-style: italic;
    }

    .token.entity {
        cursor: help;
    }

    .token.inserted {
        color: green;
    }

    /*---------- code highlight: dark theme for Gist ----------*/
    .gist .gist-file {
        border: 1px solid #555;
    }

    .gist .gist-data {
        background-color: #1e1e1e;
        border-bottom: 1px solid #555;
    }

    .gist .gist-meta {
        background-color: #424a55;
        color: #eee;
    }

    .gist .gist-meta a {
        color: #eee;
    }

    .gist .highlight {
        color: #eee;
        background-color: #1e1e1e;
    }

    .gist .blob-num {
        color: #afafaf;
    }

    .gist .blob-code-inner {
        color: #dfdfdf;
    }

    .pl-mb {
        color: #fff !important;
    }

    .pl-c {
        color: #57A64A !important;
    }

    /* comment */
    .pl-ent {
        color: #569CD6 !important;
    }

    /* entity */
    .pl-e {
        color: #9CDCFE !important;
    }

    .pl-en {
        color: #4EC9B0 !important;
    }

    /* entity attribute */
    .pl-smi {
        color: #9CDCFE !important;
    }

    .pl-k {
        color: #569cd6 !important;
    }

    .pl-c1,
    .pl-s .pl-v {
        color: #4EC9B0 !important;
    }

    .pl-pds,
    .pl-s,
    .pl-s .pl-pse .pl-s1,
    .pl-sr,
    .pl-sr .pl-cce,
    .pl-sr .pl-sra,
    .pl-sr .pl-sre,
    .pl-s .pl-s1 {
        color: #D69D85 !important;
    }

    .pl-s .pl-s1 .pl-pse {
        color: #c5dbff !important;
    }

    /* strings */
    .diff-table .pl-c,
    .diff-table .pl-ent,
    .diff-table .pl-e,
    .diff-table .pl-en,
    .diff-table .pl-pds,
    .diff-table .pl-s,
    .diff-table .pl-s .pl-s1,
    .diff-table .pl-s .pl-pse .pl-s1,
    .diff-table .pl-sr,
    .diff-table .pl-sr .pl-cce,
    .diff-table .pl-sr .pl-sra,
    .diff-table .pl-sr .pl-sre,
    .diff-table .pl-k,
    .diff-table .pl-smi,
    .diff-table .pl-c1,
    .diff-table .pl-v {
        color: #eee !important;
    }

sourceCode:
/* ==UserStyle==
@name           HackMD Night Theme
@namespace      USO Archive
@author         Lesley Lin
@description    `Night theme for HackMD`
@version        20200713.7.32
@license        CC-BY-SA-4.0
@preprocessor   uso
==/UserStyle== */
@-moz-document regexp('https:\/\/.*hackmd\.io\/.*')
{
    /*--------------- view ---------------*/
    body[style],
    body[style*="background-color: white;"] {
        background-color: #363b41 !important;
    }

    body {
        color: #363b41;
        background: #363b41;
    }

    .ui-view-area,
    .markdown-body,
    .ui-content {
        background: #363b41;
        color: #abb2bf;
    }

    h1,
    h2,
    h3,
    h4,
    h5,
    h6,
    p {
        color: #b8bfc6
    }

    /* form */
    .form-control {
        background: #333;
        color: #fff;
        border-color: #8e8e8e;
    }

    .form-control::placeholder,
    .form-control::-webkit-input-placeholder,
    .form-control:-moz-placeholder,
    .form-control::-moz-placeholder,
    .form-control:-ms-input-placeholder {
        color: #eee;
    }

    /*--------------- navbar ---------------*/
    .header {
        background-color: #2e3033;
        border-color: #2e3033;
    }

    .navbar {
        background-color: #2e3033;
        border-color: #2e3033;
    }

    .navbar a {
        color: #eee !important;
    }

    .navbar .btn-group label {
        background-color: #2e3033;
        color: #eee;
        border-color: #555;
    }

    .navbar .btn-group label.btn-default:focus,
    .navbar .btn-group label.btn-default:hover {
        background-color: #2a2a2a;
        color: #eee;
        border-color: #555;
    }

    .navbar .btn-group label.active {
        background-color: #555;
        color: #eee;
        border-color: #555;
    }

    .navbar .btn-group label.active:focus,
    .navbar .btn-group label.active:hover {
        background-color: #555;
        color: #eee;
        border-color: #555;
    }

    .navbar-default .btn-link:focus,
    .navbar-default .btn-link:hover {
        color: #eee;
    }

    .navbar-default .navbar-nav>.open>a,
    .navbar-default .navbar-nav>.open>a:focus,
    .navbar-default .navbar-nav>.open>a:hover {
        background-color: #555;
    }

    .dropdown-header {
        color: #eee;
    }

    .dropdown-menu {
        background-color: #222;
        border: 1px solid #555;
        border-top: none;
    }

    .dropdown-menu>li>a {
        color: #eee;
    }

    .dropdown-menu>li>a:focus,
    .dropdown-menu>li>a:hover {
        background-color: #555555;
        color: #eee;
    }

    .dropdown-menu .divider {
        background-color: #555;
    }

    .header .open .dropdown-menu {
        background-color: #202020;
    }

    .ui-share-menu .ui-share-copy,
    .ui-share-menu .ui-share-preview {
        border-color: #6d6d6d !important;
        background-color: #333 !important;
        color: #FFF !important;
    }

    .ui-share-menu .ui-share-copy:hover,
    .ui-share-menu .ui-share-copy:focus,
    .ui-share-menu .ui-share-preview:hover,
    .ui-share-menu .ui-share-preview:focus {
        background-color: #737373 !important;
        color: #FFF !important;
    }

    .permission-dropdown .ui-more-settings,
    .permission-dropdown .sidenav-trigger {
        color: #7bf;
    }

    .public-published-toggle .unpublish:hover {
        background-color: #286090;
    }

    .menuitem-dropdown .menuitem-dropdown-trigger {
        border-color: #8e8e8e;
    }

    .menuitem-dropdown .menuitem-dropdown-trigger:hover,
    .menuitem-dropdown .menuitem-dropdown-trigger:focus {
        background-color: #3e4045;
    }

    .navbar .announcement-popover {
        background: #4F4F4F;
    }

    .navbar .announcement-popover .announcement-popover-header {
        background: #333333;
        border-bottom: 1px solid #333333;
    }

    .navbar .announcement-popover .announcement-popover-body {
        background: #4F4F4F;
        color: #eee;
    }

    .navbar .announcement-popover .announcement-popover-footer {
        background: #4F4F4F;
    }

    .navbar .announcement-area .caption.inverse {
        color: #eee;
    }

    .label-warning {
        background-color: #ffc107;
        color: #212529;
    }

    /*--------------- history / recent ---------------*/
    .list.row-layout li .item {
        border-color: #696c7d;
    }

    .list.row-layout li:nth-last-of-type(1) .item {
        border-bottom: none;
    }

    .list li .item {
        background: #333333;
        color: #fff;
    }

    .list li:hover .item,
    .list li:focus .item {
        background: #404040;
    }

    .list li .item h4 {
        color: #b8bfc6;
    }

    .list li p {
        color: #6a6a6a;
    }

    .list li p i {
        font-style: normal;
    }

    .list li .item .content .tags span {
        background: #555;
    }

    .list li .item.wide .content .title a,
    .list li .item.wide .content .title a:focus,
    .list li .item.wide .content .title a:hover {
        color: #b8bfc6;
    }

    .ui-item {
        color: #fff;
        opacity: 0.7;
    }

    .ui-item:hover,
    .ui-item:focus {
        opacity: 1;
        color: #fff;
    }

    .list li .item.wide hr {
        border-color: #6d6d6d;
    }

    .overview-widget-group .btn,
    .multi-select-dropdown-menu .ui-dropdown-label,
    .multi-select-dropdown-menu .dropdown-options,
    .form-control {
        border-color: #8e8e8e;
    }

    .multi-select-dropdown-menu .dropdown-options .ui-option:hover {
        background-color: #4d4d4d;
        color: #eee;
    }

    #overview-control-form #overview-keyword-input-container .select2-container {
        background-color: #3e4045 !important;
    }

    #overview-control-form #overview-keyword-input-container .select2-container .select2-choices {
        background-color: #3e4045;
    }

    .search {
        background-color: #3e4045;
        color: #eee;
    }

    .btn.btn-gray {
        background: #1b1b1b;
    }

    .btn.btn-gray:hover {
        background: #4d4d4d;
        color: #eee;
    }

    .search::placeholder,
    .search::-webkit-input-placeholder,
    .search:-moz-placeholder,
    .search::-moz-placeholder,
    .search:-ms-input-placeholder {
        color: #eee;
    }

    .btn.btn-gray {
        border-color: #6d6d6d;
        background: #333;
        color: #eee;
    }

    .select2-default {
        color: #eee !important;
    }

    .select2-results .select2-highlighted {
        background: #4d4d4d;
        color: #eee;
    }

    .select2-container-multi .select2-choices {
        background: #3e4045;
    }

    .select2-container-multi .select2-choices .select2-search-choice {
        background: #131313;
        color: #eee;
        border-color: #555;
        box-shadow: none;
    }

    .btn-default,
    .btn-default:focus {
        color: #eee;
        background-color: #333333;
        border-color: #6a6a6a;
        border-radius: 0px;
    }

    .btn-default.active.focus,
    .btn-default.active:focus,
    .btn-default.active:hover,
    .btn-default:active.focus,
    .btn-default:active:focus,
    .btn-default:active:hover,
    .open>.dropdown-toggle.btn-default.focus,
    .open>.dropdown-toggle.btn-default:focus,
    .open>.dropdown-toggle.btn-default:hover {
        background: #737373;
    }

    .btn-default:hover {
        color: #fff;
        background-color: #7d7d7d;
        border-color: #6a6a6a;
    }

    .overview-widget-group .btn.active {
        background-color: #6a6a6a;
        color: #eee;
    }

    .overview-widget-group .btn:hover {
        background-color: #7d7d7d;
        color: #eee;
        border-color: #636363;
    }

    .overview-widget-group .slider.round {
        border-color: #6a6a6a;
    }

    .overview-widget-group .slider.round:before {
        border-color: #6a6a6a;
    }

    .overview-widget-group input:checked+.slider {
        background-color: #6a6a6a;
    }

    .ui-category-description-icon a {
        color: #eee;
    }

    .item .ui-history-pin.active {
        color: #f00;
    }

    .ui-history-close {
        color: #eee;
        opacity: 0.5;
    }

    .pagination>li>a,
    .pagination>li>span {
        color: #eee;
        background-color: #333333;
        border-color: #6a6a6a;
    }

    .pagination>li>a:hover {
        color: #fff;
        background-color: #7d7d7d;
        border-color: #6a6a6a;
    }

    .pagination>.disabled>a,
    .pagination>.disabled>a:focus,
    .pagination>.disabled>a:hover,
    .pagination>.disabled>span,
    .pagination>.disabled>span:focus,
    .pagination>.disabled>span:hover {
        color: #eee;
        background-color: #333333;
        border-color: #6a6a6a;
    }

    .pagination.dark>li>a,
    .pagination.dark>li>span {
        color: #aaa;
    }

    /*--------------- new overview ---------------*/
    .overview-component .list li .item{
        background: #1c1c1c;
        color: #fff;
    }

    .overview-component .list-section ul.row-layout {
        background: #1c1c1c;
        color: #737373;
        background-color: #1c1c1c;
    }

    .overview-component .list-section ul.row-layout a.title {
        color: #d6d6d6;

    }

    .overview-component .list li:hover .item,
    .overview-component .list li:focus .item {
        background: #404040;
    }

    .overview-component .list li p {
        color: #6a6a6a;
    }

    .overview-component .list li .item {
        color: #888888;
    }

    .overview-component .ui-overview-pin {
        opacity: 1;
    }

    /*--------------- settings ---------------*/
    .section .form-horizontal .form-group .btn-default {
        font-size: 16px;
        border-color: #6d6d6d;
        background-color: #333;
        color: #FFF;
    }

    .section .form-horizontal .form-group .btn-default:hover,
    .section .form-horizontal .form-group .btn-default:focus {
        background-color: #737373;
        color: #FFF;
    }

    .section .form-horizontal .form-control:focus {
        border-color: #bbb;
    }

    /*--------------- share view ---------------*/
    #notificationLabel,
    .ui-infobar .btn.ui-edit {
        color: #eee;
        border-color: #6a6a6a;
    }

    .ui-infobar__user-info li {
        color: #bbb;
    }

    footer {
        background: #101010;
        color: #bbb;
        border-top: 1px solid #454545;
    }

    footer a {
        color: #bbb;
    }

    /*--------------- doc view ---------------*/
    .markdown-body h1,
    .markdown-body h2,
    .markdown-body h3,
    .markdown-body h4,
    .markdown-body h5,
    .markdown-body h6,
    #doc>h1 {
        color: #69A2B0;
        border-color: #777 !important;
    }

    #doc>h1 {
        color: #659157;
    }

    .markdown-body hr {
        background-color: #7e7e7e;
    }

    .h1 .small,
    .h1 small,
    .h2 .small,
    .h2 small,
    .h3 .small,
    .h3 small,
    .h4 .small,
    .h4 small,
    .h5 .small,
    .h5 small,
    .h6 .small,
    .h6 small,
    h1 .small,
    h1 small,
    h2 .small,
    h2 small,
    h3 .small,
    h3 small,
    h4 .small,
    h4 small,
    h5 .small,
    h5 small,
    h6 .small,
    h6 small {
        color: #69A2B0;
    }

    .markdown-body p {
        color: #b8bfc6;
    }

    .markdown-body a {
        color: #dadada;
        text-decoration: underline;
    }

    .markdown-body a code {
        color: #dadada !important;
    }

    .markdown-body ul li,
    .markdown-body ol li {
        color: #b8bfc6;
    }

    .markdown-body ul,
    .markdown-body ol {
        padding-left: 1.3em;
    }

    .markdown-body blockquote {
        color: #b8bfc6;
        border-left-color: #777;
        font-size: 16px;
    }

    .markdown-body code,
    code {
        color: #b8bfc6 !important;
        background-color: #333333;
    }

    .markdown-body code {
        padding: 1px 2px;
    }

    .markdown-body pre {
        background-color: #333333;
        /* border: 1px solid #555 !important; */
